quiz:
{% for quiz in quizzes %}
{%- with class = quiz.__class__.__name__ -%}
{%- if class == 'MultiChoiceQuiz' -%}

  {{ quiz.title }}
  {{ quiz.question }}
  {% for key, text in quiz.choices.items() %}

    {{ text }}
    {{ quiz.feedback.get(key, '') }}

  {% endfor %}

{%- elif class == 'TrueFalseQuiz' -%}

  {{ quiz.title }}
  {{ quiz.question }}
  {% for val in [true, false] %}

    {{ 'true' if val else 'false' }}
    {{ quiz.feedback.get(true if val else false, '') }}

  {% endfor %}

{%- endif -%}
{%- endwith -%}
{% endfor %}
